            SiteEditorController.prototype.GetFolders = function ($this, parent) {
                if ($this.Folders === undefined || $this.Folders == null || $this.Folders.length <= 0) {
                    $this.Loading = true;
                }
                var path = '/';
                if (parent !== undefined && parent != null) {
                    path = parent.Path;
                    parent.Loading = true;
                }
                // create request
                var apiRequest = new SmallServerAdmin.ApiRequest($this.Context, 'Sites.GetFolders', { path: path });
                // handler successful response to a request to api
                apiRequest.SuccessCallback = function (response) {
                    //$this.Context.Timeout(() => {
                    if (parent === undefined || parent == null) {
                        $this.Folders = response.data;
                    }
                    else {
                        parent.Children = response.data;
                    }
                    //});
                };
                apiRequest.CompleteCallback = function () {
                    $this.Loading = false;
                    if (parent !== undefined && parent != null) {
                        parent.Loading = false;
                    }
                };
                // execute
                apiRequest.Execute();
            };
